import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './pages/Home';
import AdminPanel from './pages/AdminPanel';
import Login from './pages/Login';
import Header from './components/Header/Header';
import Footer from './components/Footer/Footer';

function App() {
    const [role, setRole] = useState(null);

    return (
        <Router>
            <Header /> {/* Hiển thị Header trên tất cả các trang */}
            <Routes>
                <Route path="/login" element={<Login setRole={setRole} />} />
                
                <Route 
                    path="/user" 
                    element={role === 'user' ? (
                        <div>
                            <Home />
                            {/* Các thành phần khác của user */}
                        </div>
                    ) : (
                        <Navigate to="/login" /> // Nếu không có role 'user', điều hướng về /login
                    )}
                />
                
                <Route 
                    path="/admin" 
                    element={role === 'admin' ? (
                        <AdminPanel />
                    ) : (
                        <Navigate to="/login" /> // Nếu không có role 'admin', điều hướng về /login
                    )}
                />

                <Route path="*" element={<Navigate to="/login" />} /> {/* Điều hướng tất cả các trang không tồn tại đến /login */}
            </Routes>
            <Footer /> {/* Hiển thị Footer trên tất cả các trang */}
        </Router>
    );
}

export default App;
