import React, { useState } from 'react';
import Header from '../components/Header/Header';
import Footer from '../components/Footer/Footer';
import QuestionCard from '../components/QuestionCard/QuestionCard';
import './AdminPanel.css';

const AdminPanel = () => {
    const [questions, setQuestions] = useState([
        { id: 1, text: "What happens if we don't finish?" },
        { id: 2, text: "How long do we have for the test?" },
        { id: 3, text: "Can you explain sexual reproduction" },
        // Thêm câu hỏi mẫu nếu cần
    ]);
    const [newQuestion, setNewQuestion] = useState('');

    const handleAddQuestion = () => {
        if (newQuestion.trim()) {
            setQuestions([...questions, { id: questions.length + 1, text: newQuestion }]);
            setNewQuestion('');
        }
    };

    return (
        <div>
            <Header />
            <div className="admin-content">
                <h1>Admin Panel</h1>
                <div className="question-list">
                    {questions.map((question) => (
                        <QuestionCard key={question.id} question={question} />
                    ))}
                </div>
                <div className="add-question">
                    <input
                        type="text"
                        value={newQuestion}
                        onChange={(e) => setNewQuestion(e.target.value)}
                        placeholder="Add a new question"
                    />
                    <button onClick={handleAddQuestion}>Add Question</button>
                </div>
            </div>
            <Footer />
        </div>
    );
};

export default AdminPanel;
