import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Import axios để sử dụng
import Header from '../components/Header/Header';
import Footer from '../components/Footer/Footer';
import QuestionCard from '../components/QuestionCard/QuestionCard';
import './Home.css';

const Home = () => {
    const [questions, setQuestions] = useState([]);
    const [error, setError] = useState('');  // Biến để lưu lỗi nếu có
    const [newQuestion, setNewQuestion] = useState(''); // State để lưu câu hỏi mới

    // Sử dụng useEffect để gọi API khi component được render lần đầu
    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                // Gửi yêu cầu GET đến API
                const response = await axios.get('https://66c2cb6dd057009ee9bdec9e.mockapi.io/api/Question/questions');

                // Cập nhật state với danh sách câu hỏi từ API
                setQuestions(response.data);
            } catch (err) {
                console.error('Error fetching questions:', err);
                setError('Unable to load questions. Please try again later.');  
            }
        };

        // Gọi hàm fetchQuestions
        fetchQuestions();
    }, []); // Mảng [] để đảm bảo useEffect chỉ chạy một lần khi component mount

    // Hàm để gửi câu hỏi mới đến API
    const handleAddQuestion = async () => {
        if (newQuestion.trim()) {
            try {
                // Gửi yêu cầu POST đến API để thêm câu hỏi mới
                const response = await axios.post('https://66c2cb6dd057009ee9bdec9e.mockapi.io/api/Question/questions', {
                    text: newQuestion
                });

                // Cập nhật state với danh sách câu hỏi, bao gồm câu hỏi mới
                setQuestions([...questions, response.data]);
                setNewQuestion(''); // Xóa nội dung ô nhập
            } catch (err) {
                console.error('Error adding question:', err);
                setError('Unable to add question. Please try again later.');
            }
        } else {
            setError('Question cannot be empty.');
        }
    };

    return (
        <div>
            <Header />
            <div className="question-list">
                {error && <p className="error">{error}</p>} {/* Hiển thị lỗi nếu có */}
                {questions.map((question) => (
                    <QuestionCard key={question.id} question={question} />
                ))}
            </div>
            <div className="add-question-form">
                <input
                    type="text"
                    value={newQuestion}
                    onChange={(e) => setNewQuestion(e.target.value)}
                    placeholder="Enter your question"
                />
                <button onClick={handleAddQuestion}>Add Question</button>
            </div>
            <Footer />
        </div>
    );
};

export default Home;
