import React, { useState } from 'react';
import Header from '../components/Header/Header';
import Footer from '../components/Footer/Footer';
import QuestionCard from '../components/QuestionCard/QuestionCard';
import './AdminPanel.css';

const AdminPanel = () => {
    const [questions, setQuestions] = useState([
        { id: 1, text: "What happens if we don't finish?", answer: "" },
        { id: 2, text: "How long do we have for the test?", answer: "" },
        { id: 3, text: "Can you explain sexual reproduction?", answer: "" },
        // Thêm câu hỏi mẫu nếu cần
    ]);
    const [selectedQuestionId, setSelectedQuestionId] = useState(null);
    const [answer, setAnswer] = useState('');

    const handleQuestionClick = (id) => {
        setSelectedQuestionId(id === selectedQuestionId ? null : id); // Toggle question
    };

    const handleAnswerChange = (e) => {
        setAnswer(e.target.value);
    };

    const handleSaveAnswer = (id) => {
        setQuestions(questions.map((q) =>
            q.id === id ? { ...q, answer } : q
        ));
        setAnswer('');
        setSelectedQuestionId(null); // Hide answer input after saving
    };

    return (
        <div>
            <Header />
            <div className="question-list">
                {questions.map((question) => (
                    <div key={question.id} className="question-card" onClick={() => handleQuestionClick(question.id)}>
                        <QuestionCard question={question} />
                        {selectedQuestionId === question.id && (
                            <div className="question-answer">
                                {question.answer ? (
                                    <p>{question.answer}</p>
                                ) : (
                                    <div>
                                        <textarea
                                            value={answer}
                                            onChange={handleAnswerChange}
                                            placeholder="Write the answer here..."
                                        />
                                        <button onClick={() => handleSaveAnswer(question.id)}>Save Answer</button>
                                    </div>
                                )}
                            </div>
                        )}
                    </div>
                ))}
            </div>
            <Footer />
        </div>
    );
};

export default AdminPanel;
