import React from 'react';
import './QuestionCard.css';

const QuestionCard = ({ question, onDelete, onLike, onClick }) => {
    return (
        <div className="question-card" onClick={onClick}>
            <p className="question-text">{question.text}</p>
            <div className="question-actions">
                <button className="like-button" onClick={(e) => { e.stopPropagation(); onLike(question.id); }}>
                    <i className="fas fa-heart"></i> {question.like || 0}
                </button>
                <button className="delete-button" onClick={(e) => { e.stopPropagation(); onDelete(question.id); }}>
                    <i className="fas fa-trash"></i>
                </button>
            </div>
        </div>
    );
};

export default QuestionCard;
