import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Import axios để sử dụng

const Home = () => {
    const [questions, setQuestions] = useState([]);
    const [error, setError] = useState('');

    // Sử dụng useEffect để gọi API khi component được render
    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                // Gửi yêu cầu GET đến API
                const response = await axios.get('https://api.example.com/questions');

                // Cập nhật state với danh sách câu hỏi từ API
                setQuestions(response.data);
            } catch (err) {
                console.error('Error fetching questions:', err);
                setError('Unable to load questions. Please try again later.');
            }
        };

        // Gọi hàm để fetch questions
        fetchQuestions();
    }, []); // [] để đảm bảo useEffect chỉ chạy một lần sau khi component mount

    return (
        <div>
            <h1>Question List</h1>
            {error && <p className="error">{error}</p>} {/* Hiển thị lỗi nếu có */}
            <ul>
                {questions.map((question) => (
                    <li key={question.id}>{question.text}</li>
                ))}
            </ul>
        </div>
    );
};

export default Home;
