import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from '../components/Header/Header';
import Footer from '../components/Footer/Footer';
import QuestionCard from '../components/QuestionCard/QuestionCard';
import './AdminPanel.css';

const AdminPanel = () => {
    const [questions, setQuestions] = useState([]);
    const [error, setError] = useState('');  // Variable to store error messages

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                // Make a GET request to the API to fetch questions
                const response = await axios.get('https://66c075a5ba6f27ca9a56aed0.mockapi.io/questions');

                // Update the state with the fetched questions
                setQuestions(response.data);
            } catch (err) {
                console.error('Error fetching questions:', err);
                setError('Unable to load questions. Please try again later.');
            }
        };

        fetchQuestions();  // Fetch questions when the component mounts
    }, []);

    return (
        <div>
            <Header />
            <div className="question-list">
                {error && <p className="error">{error}</p>}  {/* Display error if there's any */}
                {questions.map((question) => (
                    <QuestionCard key={question.id} question={question} />
                ))}
            </div>
            <Footer />
        </div>
    );
};

export default AdminPanel;
