import React from 'react';
import './QuestionCard.css';

const QuestionCard = ({ question, onDelete, onLike, onClick }) => {
    if (!question || !question.text) {
        return <div>Invalid question data</div>;
    }

    return (
        <div className="question-card" onClick={() => onClick(question)}>
            <p>{question.text}</p>
            <div className="question-actions">
                <button 
                    className="like-btn" 
                    onClick={(e) => { 
                        e.stopPropagation(); 
                        onLike(question.id); 
                    }}
                >
                    {question.likeCount > 0 ? 'üíñ' : '‚ù§'} {question.likeCount || 0}
                </button>
                <button className="delete-btn" onClick={(e) => { e.stopPropagation(); onDelete(question.id); }}>
                    üóëÔ∏è
                </button>
            </div>
        </div>
    );
};

export default QuestionCard;
