import React, { useState } from 'react';
import Header from '../components/Header/Header';
import Footer from '../components/Footer/Footer';
import QuestionCard from '../components/QuestionCard/QuestionCard';
import './AdminPanel.css';

const AdminPanel = () => {
    const [questions, setQuestions] = useState([
        { id: 1, text: "What happens if we don't finish?", answer: "You will receive a penalty." },
        { id: 2, text: "How long do we have for the test?", answer: "You have 2 hours to complete the test." },
        { id: 3, text: "Can you explain sexual reproduction?", answer: "Sexual reproduction involves the combination of genetic material from two parents." },
        // Thêm câu hỏi mẫu với câu trả lời nếu cần
    ]);

    const [selectedQuestion, setSelectedQuestion] = useState(null);

    const handleQuestionClick = (id) => {
        setSelectedQuestion(id === selectedQuestion ? null : id); // Toggle question
    };

    return (
        <div>
            <Header />
            <div className="admin-content">
                <h1>Admin Panel</h1>
                <div className="question-list">
                    {questions.map((question) => (
                        <div key={question.id} className="question-card" onClick={() => handleQuestionClick(question.id)}>
                            <QuestionCard question={question} />
                            {selectedQuestion === question.id && (
                                <div className="question-answer">
                                    <p>{question.answer}</p>
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            </div>
            <Footer />
        </div>
    );
};

export default AdminPanel;
