import React, { useState } from 'react';
import './QuestionCard.css';

const QuestionCard = ({ question, onDelete }) => {
    // Th√™m state ƒë·ªÉ l∆∞u tr·∫°ng th√°i "liked" v√† s·ªë l∆∞·ª£ng likes
    const [liked, setLiked] = useState(false);
    const [likesCount, setLikesCount] = useState(0);

    if (!question || !question.text) {
        return <div>Invalid question data</div>;
    }

    // H√†m x·ª≠ l√Ω khi nh·∫•n v√†o n√∫t like
    const handleLike = () => {
        setLiked(!liked); // ƒê·∫£o ng∆∞·ª£c tr·∫°ng th√°i "liked"
        setLikesCount(likesCount + (liked ? -1 : 1)); // TƒÉng ho·∫∑c gi·∫£m s·ªë l∆∞·ª£ng likes
    };

    return (
        <div className="question-card">
            <p>{question.text}</p>
            <div className="question-actions">
                <button 
                    className={`like-btn ${liked ? 'liked' : ''}`} 
                    onClick={handleLike}
                >
                    ‚ù§ {likesCount}
                </button>
                <button className="delete-btn" onClick={() => onDelete(question.id)}>
                    üóëÔ∏è
                </button>
            </div>
        </div>
    );
};

export default QuestionCard;
