import React, { useState } from 'react';
import './QuestionCard.css';

const QuestionCard = ({ question, onDelete, onLike }) => {
    const [liked, setLiked] = useState(false); // Track if the question is liked
    const [likeCount, setLikeCount] = useState(0); // Track the number of likes

    if (!question || !question.text) {
        return <div>Invalid question data</div>;
    }

    const handleLikeClick = () => {
        if (!liked) {
            setLikeCount(likeCount + 1); // Increase like count
            onLike(question.id); // Notify parent component
        } else {
            setLikeCount(likeCount - 1); // Decrease like count if already liked
        }
        setLiked(!liked); // Toggle the liked state
    };

    return (
        <div className="question-card">
            <p>{question.text}</p>
            <div className="question-actions">
                <button
                    className={`like-btn ${liked ? 'liked' : ''}`}
                    onClick={handleLikeClick}
                >
                    ‚ù§ {likeCount}
                </button>
                <button className="delete-btn" onClick={() => onDelete(question.id)}>
                    üóëÔ∏è
                </button>
            </div>
        </div>
    );
};

export default QuestionCard;
