import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';  // Import axios để gửi yêu cầu HTTP
import './Login.css';
import AmazingTechLogo from '../assets/AmazingTech.png';  // Đường dẫn chính xác từ thư mục pages đến assets

const Login = ({ setRole }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');  // Thêm state để lưu lỗi
    const navigate = useNavigate();

    const handleLogin = async () => {
        try {
            // Gửi yêu cầu POST đến API để kiểm tra đăng nhập
            const response = await axios.post('http://localhost:3000/login', {
                username,
                password
            });

            const { role } = response.data;  // Giả sử API trả về vai trò của người dùng

            if (role === 'admin') {
                setRole('admin');
                navigate('/admin');
            } else if (role === 'user') {
                setRole('user');
                navigate('/user');
            } else {
                setError('Sai thông tin đăng nhập!');
            }
        } catch (error) {
            console.error('Đã xảy ra lỗi:', error);
            setError('Đã xảy ra lỗi khi đăng nhập. Vui lòng thử lại.');
        }
    };

    return (
        <div className="login-container">
            <div className="login-logo">
                <img src={AmazingTechLogo} alt="AmazingTech Logo" />
            </div>
            <h2 className="login-title">Đăng nhập</h2>
            <div className="login-form">
                <input
                    type="text"
                    className="login-input"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
                <input
                    type="password"
                    className="login-input"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button className="login-button" onClick={handleLogin}>Đăng nhập</button>
                {error && <p className="login-error">{error}</p>}  {/* Hiển thị thông báo lỗi */}
            </div>
        </div>
    );
};

export default Login;
